    }
}

    componentDidMount(){
        axios(
          {
            url:"/product",
            baseURL:"http://127.0.0.1:8000/api",
            method:"get"
          }
         ).then((response)=>{
          console.log(response.data+"wwwwwwwwwwwwwww")
          this.setState({
       allp:response.data

          })
         
         })
    
      }
      render() {
        return (
          <div><p>{this.state.allp.concat("w")}</p></div>
        )
      }
}
